class Voiture:
    def __init__(self, marque, modele):
        self.marque = marque
        self.modele = modele

    def demarrer(self):
        print(f"{self.marque} {self.modele} démarre!")

# Créer un objet
ma_voiture = Voiture("Tesla", "Model 3")
ma_voiture.demarrer()  # Affiche: Tesla Model 3 démarre!

class CompteBancaire:
    def __init__(self, solde):
        self.__solde = solde  # Attribut privé

    def deposer(self, montant):
        if montant > 0:
            self.__solde += montant

    def get_solde(self):
        return self.__solde

class Animal:
    def __init__(self, nom):
        self.nom = nom

    def parler(self):
        pass

class Chien(Animal):
    def parler(self):
        return f"{self.nom} dit Wouf!"

class Chat(Animal):
    def parler(self):
        return f"{self.nom} dit Miaou!"

