try:
    nombre = int(input("Entrez un nombre: "))
    resultat = 10 / nombre
    print(f"Résultat: {resultat}")
except ValueError:
    print("Erreur: Vous devez entrer un nombre")
except ZeroDivisionError:
    print("Erreur: Division par zéro impossible")
finally:
    print("Fin du programme")

def verifier_age(age):
    if age < 0:
        raise ValueError("L'âge ne peut pas être négatif")
    if age < 18:
        raise Exception("Vous devez être majeur")
    return True

try:
    verifier_age(15)
except ValueError as e:
    print(f"Erreur de valeur: {e}")
except Exception as e:
    print(f"Erreur: {e}")

def tri_bulles(liste):
    n = len(liste)
    for i in range(n):
        for j in range(0, n-i-1):
            if liste[j] > liste[j+1]:
                liste[j], liste[j+1] = liste[j+1], liste[j]
    return liste

nombres = [64, 34, 25, 12, 22]
print(tri_bulles(nombres))  # [12, 22, 25, 34, 64]

def quick_sort(liste):
    if len(liste) <= 1:
        return liste

    pivot = liste[len(liste) // 2]
    gauche = [x for x in liste if x < pivot]
    milieu = [x for x in liste if x == pivot]
    droite = [x for x in liste if x > pivot]

    return quick_sort(gauche) + milieu + quick_sort(droite)

def recherche_binaire(liste, cible):
    gauche, droite = 0, len(liste) - 1

    while gauche <= droite:
        milieu = (gauche + droite) // 2

        if liste[milieu] == cible:
            return milieu
        elif liste[milieu] < cible:
            gauche = milieu + 1
        else:
            droite = milieu - 1

    return -1

liste_triee = [1, 3, 5, 7, 9, 11, 13]
print(recherche_binaire(liste_triee, 7))  # 3
